const QA = 'http://10.204.135.173:51525/utils/api/v1.0/';
const UAT = 'http://10.10.30.11:51525/utils/api/v1.0/';
const PROD = 'http://10.10.2.20:51525/utils/api/v1.0/';
const SIT = 'http://10.10.2.28:51525/utils/api/v1.0/';
const DEV = 'http://127.0.0.1:51525/utils/api/v1.0/';

module.exports = (env) => {
    let setENV;
    switch (env) {
        case 'QA':
            setENV = QA;
            break;
        case 'UAT':
            setENV = UAT;
            break;
        case 'PROD':
            setENV = PROD;
            break;
        case 'SIT':
            setENV = SIT;
            break;
        case 'DEV':
            setENV = DEV;
            break;
        default:
            setENV = SIT;
    }
    return setENV;
};