const DEV = 'http://localhost:51525/utils/api/v1.0/';
const QA = '';
const UAT = '';
const PROD = '';
const SIT = 'http://localhost:51525/utils/api/v1.0/';

module.exports = (env) => {
    let setENV;
    switch (env) {
        case 'DEV':
            setENV = DEV
        case 'QA':
            setENV = QA;
            break;
        case 'UAT':
            setENV = UAT;
            break;
        case 'PROD':
            setENV = PROD;
            break;
        case 'SIT':
            setENV = SIT;
            break;
        default:
            setENV = SIT;
    }
    return setENV;
};
