const SIT = 'http://10.10.2.28:3004/CDS';
const QA = '';
const UAT = '';
const PROD = '';
const DEV = 'http://10.10.30.11:3004/CDS';

module.exports = (env) => {
    let setENV;
    console.log("env detected ",env)
    switch (env) {
        case 'DEV':
            setENV = DEV
            break;
        case 'QA':
            setENV = QA;
            break;
        case 'UAT':
            setENV = UAT;
            break;
        case 'PROD':
            setENV = PROD;
            break;
        case 'SIT':
            setENV = SIT;
            break;
        default:
            setENV = SIT;
            break;
    }
    return setENV;
};
